<?php namespace Models;use Libs\CObject;class z5ActivitySettingModel extends CObject{    static $table = "z5_activity_setting";     /**     * User: tangwei     * Date: 2019.02.25     * @param      * @return     * Function:新增一条记录     */    public function addOne() : int     {        assert(isset($this->title) && $this->title != '' );        assert(isset($this->shareTitle) && $this->shareTitle != '' );        assert(isset($this->sharePic) && $this->sharePic != '' );        assert(isset($this->addTime) && $this->addTime != '' );        assert(isset($this->expireTime) && $this->expireTime != '' );        assert(isset($this->isOpen) && is_numeric($this->isOpen) && $this->isOpen >= 0);        assert(isset($this->isDeleted) && is_numeric($this->isDeleted) && $this->isDeleted >= 0);        $sql = "INSERT INTO ".self::$table." (`title`,`shareTitle`,`sharePic`,`addTime`,`expireTime`,`isOpen`,`isDeleted`) VALUES ( ?, ?, ?, ?, ?, ?, ?)";        $sqlParam = array(            $this->title,            $this->shareTitle,            $this->sharePic,            $this->addTime,            $this->expireTime,            $this->isOpen,            $this->isDeleted,);        return self::query($sql, $sqlParam, false);    }    /**     * User: tangwei     * Date: 2019.02.25     * @param      * @return     * Function:删除记录的方法，不进回收站，直接删数据库的记录     */    public function deleteOne() : int     {        $sql = "DELETE FROM ".self::$table." WHERE `id` = ?";        $sqlParam = array($this->id);        return self::query($sql, $sqlParam, false);    }    /**     * User: tangwei     * Date: 2019.02.25     * @param      * @return     * Function:根据ID获取一条记录     */    public static function getOneById($id) : array    {        assert(is_numeric($id) && $id > 0);        $sql = "SELECT * FROM ".self::$table." WHERE `id` = ? AND isDeleted = 0";        $sqlParam = array($id);        return self::query($sql, $sqlParam, true);    }    /**     * User: tangwei     * Date: 2019.02.25     * @param      * @return     * Function:根据ID获取一条没被删除的记录     */    public static function getOneByIdForUpdate($id) : array    {        assert(is_numeric($id) && $id > 0);        $sql = "SELECT * FROM ".self::$table." WHERE `id` = ? AND isDeleted = 0 FOR UPDATE";        $sqlParam = array($id);        return self::query($sql, $sqlParam, true);    }    /**     * User: tangwei     * Date: 2019.02.25     * @param      * @return     * Function:删除记录的方法，进回收站，不是直接删数据库的记录     */    public function deleteUpdateOne() : int    {        $sql = "UPDATE ".self::$table." SET `isDeleted` = 1 WHERE `id` = ?";        $sqlParam = array($this->id);        return self::query($sql, $sqlParam, false);    }    /**     * User: tangwei     * Date: 2019.02.25     * @param      * @return     * Function:不带limit的获取所有的没被删除的数据     */    public static function getAllUndeletedWithOutLimit() : array    {        $sql = "SELECT * FROM ".self::$table." WHERE `isDeleted` = 0 order by id desc ";        $sqlParam = array();        return self::query($sql, $sqlParam, true);    }    /**     * User: tangwei     * Date: 2019.02.25     * @param      * @return     * Function:带limit的获取所有的没被删除的数据     */    public static function getAllUndeletedWithLimit($limit) : array    {        assert($limit != "");        $sql = "SELECT * FROM ".self::$table." WHERE `isDeleted` = 0  order by id desc ".$limit;        $sqlParam = array();        return self::query($sql, $sqlParam, true);    }    /**     * User: tangwei     * Date: 2019.02.25     * @param      * @return     * Function:不带limit的获取所有的被删除的数据     */    public static function getAllDeletedWithOutLimit() : array    {        $sql = "SELECT * FROM ".self::$table." WHERE `isDeleted` = 1 order by id desc ";        $sqlParam = array();        return self::query($sql, $sqlParam, true);    }    /**     * User: tangwei     * Date: 2019.02.25     * @param      * @return     * Function:带limit的获取所有的被删除的数据     */    public static function getAllDeletedWithLimit($limit) : array    {        assert($limit != "");        $sql = "SELECT * FROM ".self::$table." WHERE `isDeleted` = 1 order by id desc ".$limit;        $sqlParam = array();        return self::query($sql, $sqlParam, true);    }    /**     * User: tangwei     * Date: 2019.02.25     * @param      * @return     * Function:编辑一条记录     */    public function editOne() : int    {        assert(isset($this->title) && $this->title != '' );        assert(isset($this->shareTitle) && $this->shareTitle != '' );        assert(isset($this->sharePic) && $this->sharePic != '' );        assert(isset($this->expireTime) && $this->expireTime != '' );        assert(isset($this->isOpen) && is_numeric($this->isOpen) && $this->isOpen >= 0);        assert(isset($this->isDeleted) && is_numeric($this->isDeleted) && $this->isDeleted >= 0);        $sql = "UPDATE ".self::$table." SET `title` = ?,`shareTitle` = ?,`sharePic` = ?,`expireTime` = ?,`isOpen` = ?,`isDeleted` = ? WHERE `id` = ? ";        $sqlParam = array(            $this->title,            $this->shareTitle,            $this->sharePic,            $this->expireTime,            $this->isOpen,            $this->isDeleted,            $this->id        );        return self::query($sql, $sqlParam, false);    }}